function sortArrayByProperty(array, property) {
  // Create a copy of the array to avoid modifying the original
  return [...array].sort((a, b) => {
    if (a[property] < b[property]) {
      return -1;
    }
    if (a[property] > b[property]) {
      return 1;
    }
    return 0;
  });
}

